import React, { Component } from 'react';
import { P, H1, H2, H3, H4, H5, H6 } from 'components/Typography';
import { StyledRow, StyledCol  } from 'components/FlexBox';
import {Button} from 'components/Button';
import styled from 'styled-components';

const Container = styled.div`
	min-height: auto;
`
const Text = styled.div`
	margin-left: 20px;
	height: auto;
	padding: 60px;
		@media (max-width: ${props => props.theme.screenSize.tablet}) {
		padding: 10px;
	}`;

const ImageContainer = styled.div`
	background-position: cover;  
	background-color: black;
	object-fit: cover;  
	height: 100%;


`;
const Image = styled.img`
	object-fit: cover;  
	height: 100%;
	// height: 500px !important;
	width: 100%;
	opacity: 0.7;

`;

const ProfileButton = styled(Button)`
	margin-top: 15px;
	margin-bottom: auto;
`;
const InfoCard = ({props, children}) =>{
	return(
			<Container>
					{
						props.right ?
							<StyledRow>
								<StyledCol  xs={12} sm={12} md={6} lg={6}>
										<ImageContainer>
											<Image src={props.image} />
										</ImageContainer>
									</StyledCol >

								<StyledCol  xs={12} sm={12} md={6} lg={6}>
										<Text >
											<H1 bold>{props.topic}</H1>
											<H4 bold>{props.ingress}</H4>
											<H5>{props.body}</H5>
											{children}
										</Text>
								</StyledCol >
							</StyledRow>
							:
							<StyledRow>
								<StyledCol  xs={12} sm={12} md={6} lg={6}>
									<Text >
										<H1 bold>{props.topic}</H1>
										<H4 bold>{props.ingress}</H4>
										<H5>{props.body}</H5>
										{children}

									</Text>
								</StyledCol >
								<StyledCol  xs={12} sm={12} md={6} lg={6}>
									<ImageContainer>
										<Image src={props.image} />
									</ImageContainer>
								</StyledCol >
							</StyledRow>
					}
			</Container>


	)
}




export default InfoCard;
