import React, { Component } from 'react';
import { P, H1, H2, H3, H4, H5, H6 } from 'components/Typography';
import { StyledRow, StyledCol  } from 'components/FlexBox';
import {Button} from 'components/Button';
import styled from 'styled-components';

const Container = styled.div`
    // border: 1px solid ${props => props.theme.Colorsors.black};
    border-radius: 5px;
`;

const Text = styled.div`
	margin-left: 20px;
`;
const ImageContainer = styled.div`
background-position: cover;  
`;
const Image = styled.img`
background-position: cover;  
`;

const ProfileButton = styled(Button)`
  margin-top: 15px;
  margin-bottom: auto;
`;
const InfoCard = (props) =>{
	// console.log(item.data.metadata) 
	return(
			<Row>
			<StyledCol  xs={6} sm={6} md={6} lg={6}>
				<ImageContainer>
					<Image src={props.image} />
				</ImageContainer>
			</StyledCol >

			<StyledCol  xs={6} sm={6} md={6} lg={6}>
				<Text >
					<H1 bold>{props.topic}</H1>
					<H3>{props.body}</H3>
				</Text>
		</StyledCol >




			</Row>


	)
}




export default InfoCard;
